# -*- coding: utf-8 -*-from django.core.urlresolvers import reversefrom django.shortcuts import get_object_or_404, redirectfrom .models import Courseclass SubdomainCourseMiddleware(object):    """    课程的子域名    """    def process_request(self, request):        host_parts = request.get_host().split('.')        if len(host_parts) > 2 and host_parts[0] != 'www':            # 从子域名获取课程            course = get_object_or_404(Course, slug=host_parts[0])            course_url = reverse('course_detail',                                 args=[course.slug])            # 重定向当前请求到course_detail, scheme的用法            url = '{}://{}{}'.format(request.scheme,                                     '.'.join(host_parts[1:]),                                     course_url)            return redirect(url)